import React, { useContext } from 'react';
import CustomView from './CustomView';
import RandomView from './RandomView';
import { Store } from '../Store';
import Landing from './Landing';


const mainView = props => {
  const { state, dispatch } = useContext(Store);

  const toggleUi = (ui) => {
    return dispatch ({
      type: 'TOGGLE_UI',
      payload: ui
    });
  };

  return (
    <div>
      {
        state.ui === 'landing' 
        ? (
        <div>
          <Landing location={props.location} />
          <h3 onClick={() => toggleUi('random')}>RANDOM PICK</h3>
          <h3 onClick={() => toggleUi('custom')}>CUSTOM SEARCH</h3>
        </div>
      ) : 
      null
      }

      {state.ui === 'custom' && <CustomView location={props.location}/>}      
      {state.ui === 'random' && <RandomView location={props.location}/>}
    </div>
  );
};

export default mainView;
