import React, { useContext, useEffect } from 'react';
import CustomView from './CustomView';
import RandomView from './RandomView';
import { Store } from '../Store';
import Landing from './Landing';


const mainView = ({ location }) => {
  const { state, dispatch } = useContext(Store);

  useEffect(() => {
    console.log('main view updated')
  }, [state.data])

  const toggleUi = (ui) => {
    return dispatch ({
      type: 'TOGGLE_UI',
      payload: ui
    });
  };

  const toggleCustomForm = () => {
    return dispatch({
      type: 'TOGGLE_CUSTOM_FORM',
    })
  }

  return (
    <div>
      {
        state.ui === 'landing' 
        ? (
        <div>
          <Landing location={location} />
          <h3 onClick={() => toggleUi('random')}>RANDOM PICK</h3>
          <h3 onClick={() => toggleUi('custom')}>CUSTOM SEARCH</h3>
        </div>
      ) : 
      null
      }

      {state.ui === 'custom' && 
        <CustomView 
          toggleCustomForm={toggleCustomForm}
          customView={state.customView}
          location={location} 
          toggleUi={toggleUi}
          data={state.data} 
        />
      } 

      {state.ui === 'random' && 
        <RandomView 
          location={location} 
          toggleUi={toggleUi} 
          data={state.data}
        />
      }

    </div>
  );
};

export default mainView;
