import React from 'react';
import { Box, Heading, TextInput, Button, ThemeContext } from 'grommet';
import { FormSearch } from 'grommet-icons';

const landing = ({ location, fetchSuccess }) => {
  // state for location (typed or fetched)
  // setState for location (if typed)
  // dispatch to state new location (if typed)

  return (
    <Box 
      pad={{ "left": "small", "right": "small" }} 
      animation="fadeIn"
      justify="center" 
      align="center" 
      height="100%"
    >
      {/* fetchSuccess || fetchSuccess === '' */}
      {fetchSuccess || fetchSuccess === '' ? 
        (
          <React.Fragment>
            <Heading 
              textAlign="center" 
              color="dark-1" 
              margin="none"
              size="small"
            >
              Where to eat in
            </Heading>
            <Heading color="status-warning" margin="none" size="large">
              {/* {location} */}
              <TextInput 
                  placeholder="e.g. Pizza, Tacos, Bar" 
                  size="small"
                  plain
                />
            </Heading>

            <Box>
              <ThemeContext.Extend value={{}}>
                <TextInput 
                  placeholder="e.g. Pizza, Tacos, Bar" 
                  size="small"
                  plain
                />
              </ThemeContext.Extend>
              <Button 
                color="status-warning"
                icon={<FormSearch />}
                alignSelf="center"
                primary
              />
            </Box>

          </React.Fragment>
        )
        : (
          <Heading>unable to fetch</Heading>
        )
      }

    </Box>
  )
}

export default landing;
