import React, { useState } from 'react';
import CustomView from './CustomView';
import RandomView from './RandomView';
import Landing from './Landing';

const mainView = props => {
  const [display, setDisplay] = useState('landing');

  return (
    <div>
      {
        display === 'landing' 
        ? (
        <div>
          <Landing location={props.location} />
          <h3 onClick={() => setDisplay('random')}>RANDOM PICK</h3>
          <h3 onClick={() => setDisplay('custom')}>CUSTOM SEARCH</h3>
        </div>
      ) : 
      null
      }

      {display === 'custom' && <CustomView location={props.location}/>}      
      {display === 'random' && <RandomView location={props.location}/>}
    </div>
  );
};

export default mainView;
