import React from 'react';

import { Box } from '../../../node_modules/grommet/components/Box';
import { Heading } from '../../../node_modules/grommet/components/Heading';

const landingButtons = ({
  validBtn, 
  toggleUi, 
  locationInput, 
  toggleLocationInputView,
}) => {
  return (
    <div className="landingButtons">
      <Box
        align="center"
        justify="center"
        height={locationInput ? "65%" : "50%"}
        background={{ 
          'opacity': validBtn() ? 'strong' : 'medium', 
          'color': 'status-warning'
        }}
        onClick={validBtn() ? () => toggleUi('random') : null}
      >
        <Heading 
          level="3"
          size="small" 
          textAlign="center" 
          color={validBtn()  ? "" : "dark-6"}
          >
          FIND ME PLACES
        </Heading>
      </Box>
      <Box
        height="35%"
        align="center"
        justify="center"
        background={{
          'opacity': validBtn() ? 'strong' : 'medium',
          'color': 'accent-4'
        }}
        onClick={validBtn() ? () => toggleUi('custom') : null}
      >
        <Heading 
          level="3"
          size="small" 
          textAlign="center" 
          color={validBtn() ? "" : "dark-6"}
        >
          CUSTOM SEARCH
        </Heading>
      </Box>
      {!locationInput && (
        <Box 
          height="20%" 
          justify="center" 
          background="dark-1" 
          onClick={() => toggleLocationInputView(true)}
        >
          <Heading 
            level="5" 
            size="xsmall" 
            margin="none" 
            textAlign="center"
            alignSelf="center"
          >
            WRONG LOCATION? FIX IT HERE
          </Heading>
        </Box>
      )}
    </div>
  )
}

export default landingButtons;
