import React, { useContext, useEffect } from 'react';

import ResultView from './ResultView';
import { Store } from '../Store';

const randomView = ({ location, toggleUi, data, locationInput }) => {
  const { dispatch } = useContext(Store);
  useEffect(() => fetchRandomData(), []);
  // location es input location o la location del fetch
  // locationInput es true or false si esta en modo
  const fetchRandomData = () => dispatch ({ type: 'FETCH_RANDOM_DATA', payload: { locationInput, location }});

  return (
    <ResultView 
      location={location} 
      toggleUi={toggleUi}
      isCustom={false} 
      randomSorting
      data={data} 
    />
  );
};

export default randomView;
