import React, { useState, useEffect, useContext } from 'react';
import { Store } from '../Store';



// 1 TERM && cualquier criterio menos random
// RETURN LOS PRIMERO 3 DE ARRAY DE RESPUESTA

// 1 TERM && random
// RETURN RANDOM 3 ITEMS

// 2 TERM && cualquier criterio menos random
// RETURN EL PRIMERO DE 1 Y LOS PRIMEROS 2 DE OTRO (cual tiene 1 y cual 2 luego se define)

// 2 TERM && random
// RETURN RANDOM 1 DE 1 Y 2 RANDOM DE OTRO

// 3 TERM && cualquier criterio menos random
