import React, { useReducer, createContext } from "react";

const initialState = {
  customView: {
    resultsMode: false,
    searchTerms: [],
    sortBy: 'best_match'
  },
  data: [],
  ui: 'landing'
}



const randomSorting = () => {
  let criteria = ['best_match', 'rating', 'review_count', 'distance'];
  let index = Math.floor(Math.random() * 4);
  return criteria[index];
};


const searchTerm = async (term, sort_by) => {
  const response = await fetch('/search', {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify({ 
      term,
      sort_by
    })
  });
  const businesses = await response.json();
  return businesses.businesses;
};


  const fetchData = async (searchInputs) => {
    let sorting = searchInputs.sortBy === 'random' ? randomSorting() : searchInputs.sortBy
    let data = [];
    if (searchInputs.searchTerms.length === 0) {
      data = await searchTerm(null, sorting);
    } else {  
      // const asyncForEach = async (array, callback) => {
      //   for (let index = 0; index < array.length; index++) {
      //     await callback(array[index], index, array);
      //   }
      // }

      // const fetchMultiple = async () => {
      //   await asyncForEach(searchInputs.searchTerms, async (el) => {
      //     //let searchResult = searchTerm(el, sorting);
      //     data.push(await searchTerm(el, sorting));
      //   });
      // }

      // fetchMultiple();

      // const fetchMultiple = async () => {
      //   let newData = await [];

      //   searchInputs.searchTerms.forEach(async function(item) {
      //     let searchResult = await searchTerm(item, sorting);
      //     newData.push(searchResult);
      //   });

      //   console.log(newData);
      // }

      // fetchMultiple();

      searchInputs.searchTerms.forEach(async function(item) {
        let searchResult = await searchTerm(item, sorting);
        return searchResult;
      }).then(response => console.log(response));


    }
    console.log(data);
    return data;
  };


  const fetchRandomData = async () => {
    const response = await fetch('/getRandom');
    const businesses = await response.json();
    return businesses.businesses;
  };



function reducer(state, action) {
  let customViewCopy = {...state.customView};

  switch(action.type) {
    case 'TOGGLE_CUSTOM_FORM':
      customViewCopy.resultsMode = !customViewCopy.resultsMode;
      return { ...state, customView: customViewCopy};

    case 'SUBMIT_SEARCH':
      customViewCopy = {
        searchTerms: action.payload.searchTerms,
        sortBy: action.payload.sortBy,
        resultsMode: true
      }

      return {...state, customView: customViewCopy, data: fetchData(action.payload)};

    case 'FETCH_RANDOM_DATA':
      return {...state, data: fetchRandomData()};

    case 'TOGGLE_UI':
      customViewCopy = {
        resultsMode: false,
        searchTerms: [],
        sortBy: 'best_match'
      }

      return {...state, customView: customViewCopy, data: [], ui: action.payload};

    default:
      return state;
  }
}

export function StoreProvider(props) {
  const [state, dispatch] = useReducer(reducer, initialState)
  const value = {state, dispatch}
  return <Store.Provider value={value}>{props.children}</Store.Provider>
}

export const Store = createContext();
