        //           size="small" 
        //           color="dark-1"
        //           icon={<Previous />}
        //           margin={{ top: '4px' }}
        //           onClick={() => toggleLocationInputView(false)}
        //         />
        //       </Box>
        //     )}
        //     <Landing 
        //       location={location} 
        //       fetchSuccess={fetchSuccess} 
        //       locationInput={locationInput}
        //     /> 
        //   <Box height="40vh" animation="slideUp">
        //     <Box
        //       align="center"
        //       justify="center"
        //       height={locationInput ? "65%" : "50%"}
        //       background={{ 
        //         'opacity': validBtn() ? 'strong' : 'medium', 
        //         'color': 'status-warning'
        //       }}
        //       onClick={validBtn() ? () => toggleUi('random') : null}
        //     >
        //       <Heading 
        //         level="3"
        //         size="small" 
        //         textAlign="center" 
        //         color={validBtn()  ? "" : "dark-6"}
        //         >
        //         FIND ME PLACES
        //       </Heading>
        //     </Box>
        //     <Box
        //       height="35%"
        //       align="center"
        //       justify="center"
        //       background={{
        //         'opacity': validBtn() ? 'strong' : 'medium',
        //         'color': 'accent-4'
        //       }}
        //       onClick={validBtn() ? () => toggleUi('custom') : null}
        //     >
        //       <Heading 
        //         level="3"
        //         size="small" 
        //         textAlign="center" 
        //         color={validBtn() ? "" : "dark-6"}
        //       >
        //         CUSTOM SEARCH
        //       </Heading>
        //     </Box>
        //     {!locationInput && (
        //       <Box 
        //         height="20%" 
        //         justify="center" 
        //         background="dark-1" 
        //         onClick={() => toggleLocationInputView(true)}
        //       >
        //         <Heading 
        //           level="5" 
        //           size="xsmall" 
        //           margin="none" 
        //           textAlign="center"
        //           alignSelf="center"
        //         >
        //           WRONG LOCATION? FIX IT HERE
        //         </Heading>
        //       </Box>
        //     )}
        //   </Box>
        // </div>

        <LandingView 
          location={location} 
          validBtn={validBtn}
          toggleUi={toggleUi}
          fetchSuccess={fetchSuccess} 
          locationInput={locationInput}
          toggleLocationInputView={toggleLocationInputView}
        />

      )}
      {state.ui === 'custom' && (
        <CustomView 
          data={state.data}
          toggleUi={toggleUi}
          customView={state.customView}
          toggleCustomForm={toggleCustomForm}
          location={locationInput ? state.inputLocation : location}
        />
      )} 
      {state.ui === 'random' && (
        <RandomView 
          data={state.data}
          locationInput={locationInput}
          toggleUi={() => toggleUi('landing')} 
          location={locationInput ? state.inputLocation : location}
        />
      )}
    </>
  );
};

export default mainView;
