import React, { Component } from 'react';
import Main from './components/main';

class App extends Component {
  state = {
    response: '',
  };

  // handleSubmit = async e => {
  //   e.preventDefault();
  //   const response = await fetch('/api/world', {
  //     method: 'POST',
  //     headers: {
  //       'Content-Type': 'application/json',
  //     },
  //     body: JSON.stringify({ post: this.state.post }),
  //   });
  //   const body = await response.text();
  //   this.setState({ responseToPost: body });
  // };

  getJson = async e => {
    e.preventDefault();
    const response = await fetch('/getJson', {
      method: 'GET'
    });
    const body = await response.text();
    this.setState({ response: JSON.parse(body) });
  };

  logState = () => {
    console.log('LOGING COMPONENT STATE');
    console.log(this.state.response);
  }



render() {
    return (
      <div>
        <Main />
        <form onSubmit={this.getJson}>
          <p>
            <strong>Test backend fetching</strong>
          </p>
          <button type="submit">Test</button>
          <button onClick={this.logState}>Console log component state</button>
        </form>
        {/* <p>{this.state.response}</p> */}
      </div>
    );
  }
}
export default App;
