import React, { Component } from 'react';

class ResultList extends Component {
  state = {
    data: [],
    displayItems: [],
    isRandom: true
  }

  getIndeces = (businesses) => {
    let indeces = [];
    if (businesses.length < 3) {
      indeces.push(0, 1);
    } else {
      while (indeces.length < 3) {
        let number = Math.floor(Math.random() * Math.floor(businesses.length));
        if (!indeces.includes(number)) {
          indeces.push(number);
        }
      };
    }
    return indeces;
  }


  randomPicks = () => {
    let results = this.getIndeces(this.props.data).map(el => {
      return this.props.data[el];
    })
    return results;
  }


  setInitialStates(inputData, isRandom) {
    let displayItems = [];
    let data = [];

    if (this.props.data.length < 3) {
      displayItems = inputData;
    } else {

      if(isRandom) {
        displayItems = this.randomPicks();
      } else {
        for (let i=0; i<3; i++) {
          displayItems.push(inputData[i]);
        }
      }
      data = inputData.filter(el => displayItems.includes(el) === false);
    }
    this.setState({ data });
    this.setState({ displayItems }); 
  }


  componentDidUpdate(prevProps) {
    if (this.props.data !== prevProps.data) {
      // CONNECT IF RANDOM OR NOT FROM TOP PROPS
      this.setInitialStates(this.props.data, false);
    }
  }  



  // when an item in display array is clicked
    // delete from display array
    // (depending on random or not) take item from data log and add to display array

    // SETS 3 RANDOM PICKS AND ADDS THEM TO STATE


  // TAKE FULL DATA [1,2,3,4,5,6]
  // SELECT 3 ITEMS AND UPDATE DATA
  // REFRESH DATA ARRAY EACH TIME ONE ITEM IS DELETED

  // ONLY SHOW 3 RANDOM PICKS
  // WHEN USER CLICKS IN ONE ITEM IT DELETES AND GIVES ANOTHER RANDOM FROM THE LIST




  removeItem = (passedIndex) => {
    // DELETE CLICKED ITEM FROM NEW DISPLAY ITEMS
    let newDisplayItems = this.state.displayItems.filter((el, index) => passedIndex !== index);
    // MAKE COPY OF THIS.STATE.DATA TO VARIABLE
    let newState = [...this.state.data];
    let newItem;

    // IF RANDOM
    if (this.state.isRandom) {
      // SELECT RANDOM ITEM FROM NEW STATE AND SAVE IT TO NEW ITEM
      newItem = newState[this.getIndeces(newState)[0]];

      console.log(newState);
      console.log(newItem);
      // UPDATE NEW STATE, FILTERING OUT THE NEW ITEM
      newState = newState.filter(el => newItem.name !== el.name);
    } else {
      // SAVE VALUE OF REMOVED ITEM FROM ARRAY WITH SHIFT
      newItem = newState.shift();
    }

    if (newItem !== undefined) {
      newDisplayItems.push(newItem);
    }

    this.setState({ data: newState });
    this.setState({ displayItems: newDisplayItems });
  }


  render() {
    return (
      <div>
        <button onClick={() => console.log(this.state)}>GET STATE</button>
      <ul>
        {
          this.state.displayItems.length === 0 ?
          // ADD NO BUSINESSES COMPONENT
          <h1>There are no businesses</h1> :
          // ELSE RETURN DISPLAY ITEMS
          this.state.displayItems.map((el, index) => {
            return <li key={index} onClick={() => this.removeItem(index)}>{el.name}</li>
          })
        }
      </ul>
      </div>
    );
  }
};

export default ResultList;
