import React, { useContext, useEffect } from 'react';

import ResultView from './ResultView';
import { Store } from '../Store';

const randomView = ({ location, toggleUi, data, locationInput }) => {
  const { dispatch } = useContext(Store);
  //useEffect(() => fetchRandomData(), []);
  //const fetchRandomData = () => dispatch ({ type: 'FETCH_RANDOM_DATA', payload: locationInput });

  let coordinates = { lat: '', lon: '' };
  const fetchRandomData = () => dispatch ({ type: 'FETCH_RANDOM_DATA', payload: { locationInput, coordinates } })

  const geoFindMe = () => {
    const success = async (position) => {
      const lat = await position.coords.latitude;
      const lon = await position.coords.longitude;
      return { lat, lon };
    }

    function error() {
      return "error";
    }

    if (!navigator.geolocation) {
      error();
    } else {
      navigator.geolocation.getCurrentPosition(success, error);
    }
  }


  useEffect(() => {
    if(!locationInput) {
      coordinates = geoFindMe();
      console.log(coordinates);
    }
    fetchRandomData();
  }, [coordinates]);

  return (
    <ResultView 
      location={location} 
      toggleUi={toggleUi}
      isCustom={false} 
      randomSorting
      data={data} 
    />
  );
};

export default randomView;
