import React, { useContext, useEffect } from 'react';
import ResultView from './ResultView';
import { Store } from '../Store';

// LOGIC COMPONENT THAT FETCHES DATA IN RANDOM MODE AND PASSES DATA TO RESULT VIEW

const randomView = ({ location }) => {
  const { state, dispatch } = useContext(Store);
  //useEffect(() => fetchRandomData(), []);
  useEffect(() => fetchRandomData, [state.data, dispatch]);

  const fetchRandomData = () => {
    console.log('FETCH RANDOM DATA TRIGGERED');
    return dispatch ({
      type: 'FETCH_RANDOM_DATA',
    });
  };

  return (
    <ResultView 
      location={location} 
      data={state.data} 
      isCustom={false} 
      randomSorting={true}
    />
  );
};

export default randomView;
