import React from 'react';

import { Box, Heading } from 'grommet';

const ListItem = ({ item, removeItem }) => {
  return (
    <Box background="accent-4" round="small" width="100vw" height="20%">
      <Heading size="small">{item.name}</Heading>
      <h6>{item.categories[0].title}</h6>
      <h6>{(item.distance / 1000).toFixed(1)} kms</h6>
      <h6><a href={`tel:${item.phone}`}>Hablar a negocio</a></h6>
      <button onClick={() => removeItem()}>Delete Item</button>
    </Box>
  );
};

export default ListItem;
