import React, { Component } from 'react';
import ResultView from './ResultView';

// LOGIC COMPONENT THAT FETCHES DATA IN RANDOM MODE AND PASSES DATA TO RESULT VIEW

class RandomView extends Component {
  state = {
    data: []
  };

  // API GET REQUEST FETCHES 3 RANDOM LOCAL BUSINESSES FROM COORDINATES
  getBusinesses = async () => {
    const response = await fetch('/getRandom');
    const businesses = await response.json();
    this.setState({ data: businesses.businesses });
  };

  componentDidMount () {
    this.getBusinesses();
  };

  render() {
    return (
      <ResultView 
        location={this.props.location} 
        data={this.state.data} 
        custom={false} 
        isRandom={true}
      />
    );
  }; 
};

export default RandomView;
