import React from 'react';

import { Box, Heading, Text, Markdown } from 'grommet';

const ListItem = ({ item, removeItem }) => {
  return (
    <Box background="accent-4" round="small" width="80%" height="33%" align="center" >
      <Text weight="bold">{item.name}</Text>
      <Text size="small">{item.categories[0].title}</Text>
      <Text>{(item.distance / 1000).toFixed(1)} kms</Text>
      <Text><a href={`tel:${item.phone}`}>Hablar a negocio</a></Text>
      <button onClick={() => removeItem()}>Delete Item</button>
    </Box>
  );
};

export default ListItem;
