import React from 'react';
import { Box } from 'grommet';
import { Previous, Anchor } from 'grommet-icons';

const resultHeader = ({ 
  customSearchData, 
  toggleCustomForm, 
  location, 
  isCustom, 
  toggleUi 
}) => {
  return (
    <React.Fragment>
      {!isCustom && 
        <Box height="20%" background="status-warning">
          <Box 
            background={{ "color": "dark-1", "opacity": "weak"}}
            round="large" 
            width="66px" 
            pad="small" 
          >
            <Anchor 
              alignSelf="center" 
              icon={<Previous />}
              color="dark-1" 
              size="small" 
            />
          </Box>




          <h3>Searching for eating options in {location}</h3>
          <button onClick={() => toggleUi('landing')}>MAIN PAGE</button>   
        </Box>
      }

      {isCustom && 
        <Box height="60%" background="status-warning">
          <ul>
            {customSearchData.searchTerms.map((el, index) => {
              return <li key={index}>{el}</li>
            })} 
          </ul>         
          <h6>Sorting by: {customSearchData.sortBy}</h6>
          <button onClick={() => toggleCustomForm()}>EDIT SEARCH CRITERIA</button>
        </Box>
      }
    </React.Fragment>
  );
};

export default resultHeader;
