import React, { useState, useEffect } from 'react';


// FIRST GET INPUT DATA 3 DISPLAY ITEMS AND EVERYTHING ELSE AS DATA
  // IF IS RANDOM MODE GET 3 

// NO TERM && cualquier criterio menos random
// RETURN LOS PRIMEROS 3 DE ARRAY DE RESPUESTA

// NO TERM && random
// RETURN RANDOM 3 ITEMS

// 1 TERM && cualquier criterio menos random
// RETURN LOS PRIMERO 3 DE ARRAY DE RESPUESTA

// 1 TERM && random
// RETURN RANDOM 3 ITEMS

// 2 TERM && cualquier criterio menos random
// RETURN EL PRIMERO DE 1 Y LOS PRIMEROS 2 DE OTRO (cual tiene 1 y cual 2 luego se define)

// 2 TERM && random
// RETURN RANDOM 1 DE 1 Y 2 RANDOM DE OTRO

// 3 TERM && cualquier criterio menos random
// RETURN el primero de cada term

// 3 TERM && random 
// RETURN y 



const resultList = ({ data, randomSorting, isCustom }) => {
  const [businesses, setBusinesses] = useState([]);
  const [displayItems, setDisplayItems] = useState([]);


  ////////  WORKING SOLUTION IN RANDOM MODE /////////////
  // useEffect(() => {
  //   async function fetchData() {
  //     let newData = await data;
  //     setBusinesses(newData);
  //   }

  //   fetchData();
  // }, [data, setBusinesses]);





  // funciona custom pero sin terms
  useEffect(() => {
    async function fetchData() {
      let newData = await data;
      setBusinesses(newData);
    }


    async function fetchCustomData() {
      let resultArray = [];

      for (let i=0; i<data.length; i++) {
        resultArray = await [...resultArray, ...data[i]];
      }

      console.log(resultArray);
      setBusinesses(resultArray);
    }


    if (isCustom) {
      fetchCustomData();
    } else {
      fetchData();
    }

    console.log(businesses);
  }, [data, setBusinesses]);










  // returns 3 random indeces depending on input array length
  const getIndeces = (businesses) => {
    let indeces = [];
    if (businesses.length < 3) {
      indeces.push(0, 1);
    } else {
      while (indeces.length < 3) {
        let number = Math.floor(Math.random() * Math.floor(businesses.length));
        if (!indeces.includes(number)) {
          indeces.push(number);
        }
      };
    }
    return indeces;
  }

  // returns 3 random array items using get indeces function
  const randomPicks = (array) => {
    let results = this.getIndeces(array).map(el => {
      return array[el];
    })
    return results;
  }


  // IF IS IN CUSTOM MODE
  const setStatesCustom = (inputData) => {
    // 1 array only
    console.log('set states custom');
  }

  // IF IS RANDOM MODE
  const setStatesRandom = (inputData) => {
    let displayItems = [];
    let data = [];

    if (inputData.length < 3) {
      displayItems=inputData;
    } else {
      if(randomSorting) {
        displayItems=randomPicks(inputData);
      } else {
        for (let i=0; i<3; i++) {
          displayItems.push(inputData[i]);
        }
      }
      data = inputData.filter(el => displayItems.includes(el) === false);
    }

    setBusinesses(data);
    setDisplayItems(displayItems);
  }



  const setInitialStates = (inputData) => {
    let displayItems = [];
    let data = [];


    // loop through input data [ [1,2,3], [1,1,1], ['a','b','c'] ]
    if (inputData.length < 3) {
      displayItems = inputData;

    } else {

      if(randomSorting) {
        displayItems = randomPicks(inputData);

      } else {
        for (let i=0; i<3; i++) {
          displayItems.push(inputData[i]);
        }

      }
      data = inputData.filter(el => displayItems.includes(el) === false);

    }

    //displayItems = inputData;
    console.log('SETTING INITIAL STATES')
    setBusinesses(data);
    setDisplayItems(displayItems);
  }






  ///////// FUNCTION TO REMOVE ITEMS BY CLICKING EACH CARD AND UPDATE EACH STATE ///////////////////
  // removeItem = (passedIndex) => {
  //   // update display items by deleting clicked item
  //   let newDisplayItems = this.state.displayItems.filter((el, index) => passedIndex !== index);
  //   // copy state
  //   let newState = [...this.state.data];
  //   let newItem;
  //   // if random mode is selected
  //   if (this.props.randomSorting) {
  //     // select random item from new state and save it to new item
  //     newItem = newState[this.getIndeces(newState)[0]];
  //     // update new state, filtering out the new item
  //     newState = newState.filter(el => newItem.name !== el.name);
  //   } else {
  //     // save value of removed item from array with shift
  //     newItem = newState.shift();
  //   }

  //   if (newItem !== undefined) {
  //     newDisplayItems.push(newItem);
  //   }

  //   this.setState({ data: newState });
  //   this.setState({ displayItems: newDisplayItems });
  // }



  return (
    <div>
      <button onClick={() => console.log(businesses)}>GET BUSINESSES STATE</button>
      <button onClick={() => console.log(displayItems)}>GET DISPLAY ITEMS STATE</button>

      <ul>
        {
          businesses.map((el, index) => {
            return <li key={index}>{el.name}</li>
          })
        }
      </ul>
    </div>
  );
};

export default resultList;
