import React, { useState, useEffect } from 'react';
import { Box, Heading, TextInput, Button, ThemeContext } from 'grommet';
import { FormSearch } from 'grommet-icons';

const landing = ({ location, fetchSuccess }) => {
  const [locationInput, setLocation] = useState();
  useEffect(() => { setLocation(location) }, [location]);

  useEffect(() => {
    if (locationInput === '' || locationInput === undefined) { 
      setLocation(location); 
    } else {
      // let newInfo = capitalize(locationInput);
      let newLocation = locationInput.charAt(0).toUpperCase() + locationInput.substring(1)
      setLocation(newLocation);
    }
  }, [locationInput]);

  // dispatch to state new location (if typed)


  return (
    <Box 
      pad={{ "left": "small", "right": "small" }} 
      animation="fadeIn"
      justify="center" 
      align="center" 
      height="100%"
    >
      {fetchSuccess || fetchSuccess === '' ? 
        (
          <React.Fragment>
            <Heading 
              textAlign="center" 
              color="dark-1" 
              margin="none"
              size="small"
            >
              Where to eat in
            </Heading>
            <Heading color="status-warning" margin="none" size="large" textAlign="center">
              {locationInput}
            </Heading>

            <Box>
              <ThemeContext.Extend value={{}}>
                <TextInput
                  onChange={(e) => setLocation(e.target.value)}
                  placeholder="Wrong location? Fix it here" 
                  size="small"
                  plain
                />
              </ThemeContext.Extend>
              <Button 
                color="status-warning"
                icon={<FormSearch />}
                alignSelf="center"
              />
            </Box> 
          </React.Fragment>
        )
        : (
          <Heading>unable to fetch</Heading>
        )
      }

    </Box>
  )
}

export default landing;
