import React, { useEffect } from 'react';

const styles = {
  border: '1px solid black',
  margin: '12px',
  textAlign: 'center'
};

const ListItem = ({ item }) => {
  let titles = item.categories;

  useEffect(() => {
    console.log(item.categories);
    titles = item.categories;
  }, [titles]);

  return (
    <div style={styles}>
      <h5>{item.name}</h5>
      <h6>{item.distance / 1000} kms</h6>
      <h6>{titles[0].title}</h6>
    </div>
  );
};

export default ListItem;
