import React, { Fragment, useState } from 'react';

// updates state with pertaining custom search info
  // when user clicks submit, action is dispatched to fetch data from store


const customForm = () => {

  // data = from state
  const [searchTerms, setSearchTerms] = useState([]);
  const [currentTerm, setCurrentTerm] = useState("");
  const [sortBy, setSortBy] = useState('best_match');

   // // ADDS TERM TO STATE
  // addTerm = e => {
  //   e.preventDefault();
  //   if (this.state.currentTerm !== '') {
  //     if (this.state.searchTerms.length < 3) {
  //       let newTerms = [...this.state.searchTerms];
  //       newTerms.push(this.state.currentTerm);

  //       this.setState({ searchTerms: newTerms });
  //       this.setState({ currentTerm: '' });
  //     };
  //   }; 
  // };


  const addTerm = (e) => {
    e.preventDefault();

    if (currentTerm !== '') {
      if (searchTerms.length < 3) {
        // hacer copia de searchTerms
        let newTerms = [...searchTerms];
        // push copy into searchTerms
        newTerms.push(currentTerm);
        // set searchTerms al nuevo array
        setSearchTerms(newTerms);
        // set currentTerm a vacio otra vez
        setCurrentTerm('');
      }
    }
  }

  return (
    <Fragment>
      <h1>Custom Form</h1>

      {/* onSubmit fetch data */}
      <form>
          <label>
            Name:
            <input 
              type="text" 
              placeholder="Type Search Term" 
              onChange={(e) => setCurrentTerm(e.target.value)} 
            />
            <button onClick={(e) => addTerm(e)}>Add term</button>
          </label>

          {
            searchTerms.map((el, index) => {
              // on click remove term with index
              return <p key={index}>{el}</p>
            })
          }

          {/* <label>
            Sort by:
            <select name="sortBy" value={this.state.sortBy} onChange={this.handleInputChange} >
              <option value="best_match">Best Match</option>
              <option value="rating">Rating</option>
              <option value="review_count">Review Count</option>
              <option value="distance">Distance</option>
              <option value="random">Random</option>
            </select>
          </label>

          <input type="submit" value="Submit" /> */}
        </form>
    </Fragment>
  );
};

export default customForm;
