import React, { useState, useEffect } from 'react';

const styles = {
  border: '1px solid black',
  margin: '12px',
  textAlign: 'center'
};

const ListItem = ({ item }) => {
  const [expandCard, setExpandCard] = useState(true);


  return (
    <div style={styles} onClick={() => setExpandCard(!expandCard)}>
      <h5>{item.name}</h5>
      <h6>{item.distance / 1000} kms</h6>

      {expandCard && (
        <div>
          <h6>Llamar</h6>
          <h6>Direcciones</h6>
        </div>
      )}

    </div>
  );
};

export default ListItem;
