import React from 'react';
import { Box, Anchor, Text } from 'grommet';
import { Previous } from 'grommet-icons';

const resultHeader = ({ 
  customSearchData, 
  toggleCustomForm, 
  location, 
  isCustom, 
  toggleUi 
}) => {
  return (
    <React.Fragment>
      {!isCustom && 
        <Box 
          background="status-warning" 
          justify="evenly"
          direction="row" 
          align="center"
          height="10%" 
          pad="small"
        >
          <Box width="20%">       
            <Anchor 
              onClick={() => toggleUi('landing')}
              margin={{ top: "4px" }}
              icon={<Previous />}
              color="dark-1"
              size="small" 
            />
          </Box>
          <Box width="80%" pad={{ right: "medium" }} direction="row" justify="end" align="end">
            <Text size="small">Eating in</Text>
            <Text size="medium" weight="bold" weight="bold">{location}</Text>
          </Box>
        </Box>
      }

      {isCustom && 
        <Box height="60%" background="status-warning">
          <ul>
            {customSearchData.searchTerms.map((el, index) => {
              return <li key={index}>{el}</li>
            })} 
          </ul>         
          <h6>Sorting by: {customSearchData.sortBy}</h6>
          <button onClick={() => toggleCustomForm()}>EDIT SEARCH CRITERIA</button>
        </Box>
      }
    </React.Fragment>
  );
};

export default resultHeader;
