import React, { useContext, useEffect } from 'react';

import ResultView from './ResultView';
import { Store } from '../Store';

const randomView = ({ location, toggleUi, data, locationInput }) => {
  const { dispatch } = useContext(Store);
  //useEffect(() => fetchRandomData(), []);
  useEffect(() => {
    console.log('im called twice');
    fetchRandomData();
  }, [])
  const fetchRandomData = () => dispatch ({ type: 'FETCH_RANDOM_DATA', payload: locationInput });

  return (
    <ResultView 
      location={location} 
      toggleUi={toggleUi}
      isCustom={false} 
      randomSorting
      data={data} 
    />
  );
};

export default randomView;
