import React from 'react';
import { Box, Anchor, Text } from 'grommet';
import { Previous } from 'grommet-icons';

const resultHeader = ({ 
  customSearchData, 
  toggleCustomForm, 
  location, 
  isCustom, 
  toggleUi 
}) => {
  return (
    <React.Fragment>
      {!isCustom && 
        <Box 
          pad={{ left: "small", right: "small" }}
          alignContent="center"
          background="light-1"
          justify="around"
          direction="row" 
          align="center"
          height="15%" 
        >
          <Box width="20%">       
            <Anchor 
              onClick={() => toggleUi('landing')}
              margin={{ top: "4px" }}
              icon={<Previous />}
              color="dark-1"
              size="small" 
            />
          </Box>
          <Box width="60%" align="center">
            <Text size="small" weight="bold">
              Eating in
            </Text>
            <Text size="large" weight="bold" color="status-warning">
              {location}
            </Text>
          </Box>
          <Box width="20%"></Box>
        </Box>
      }

      {isCustom && 
        <Box 
          pad={{ left: "small", right: "small" }}
          alignContent="center"
          background="light-1"
          justify="around"
          align="center"
          height="30%" 
        >
          {/* <ul>

          </ul>         
          <h6>Sorting by: {customSearchData.sortBy}</h6> */}
          <Box height="50%" direction="row" width="100%" align="center">
            <Box width="20%">       
              <Anchor 
                onClick={() => toggleCustomForm()}
                margin={{ top: "4px" }}
                icon={<Previous />}
                color="dark-1"
                size="small" 
              />
            </Box>
            <Box width="60%" align="center">
              <Text size="small" weight="bold">
                Eating in
              </Text>
              <Text size="large" weight="bold" color="status-warning">
                {location}
              </Text>
            </Box>
            <Box width="20%"></Box>
          </Box>

          <Box height="50%" width="100%">
            <Box direction="row" justify="evenly">
              {customSearchData.searchTerms.map((el, index) => {
                return (
                  <Box 
                    pad={{ top: "xsmall", bottom: "xsmall", left: "small", right: "small" }}
                    background="light-3" 
                    round="xlarge"
                    key={index} 
                  >
                    <Text textAlign="center" weight="bold" size="small">
                      {el}
                    </Text>
                  </Box>
                )
              })} 
            </Box>

          </Box>
        </Box>
      }
    </React.Fragment>
  );
};

export default resultHeader;
