import React, { useState, useContext } from 'react';
import { Store } from '../Store';

const customForm = ({ toggleUi, customView }) => {
  const { dispatch } = useContext(Store);
  const [searchTerms, setSearchTerms] = useState(customView.searchTerms);
  const [currentTerm, setCurrentTerm] = useState("");
  const [sortBy, setSortBy] = useState(customView.sortBy);

  const submitSearchTerms = (e) => {
    e.preventDefault();
    let payload = { sortBy, searchTerms };
    return dispatch ({
      type: 'SUBMIT_SEARCH',
      payload
    });
  };

  const addTerm = (e) => {
    e.preventDefault();
    if (currentTerm !== '') {
      if (searchTerms.length < 3) {
        let newTerms = [...searchTerms];
        newTerms.push(currentTerm);
        setSearchTerms(newTerms);
        setCurrentTerm('');
      }
    }
  };

  const removeTerm = (inputIndex) => {
    let newSearchTerms = [...searchTerms].filter((el, inx) => inx !== inputIndex);
    setSearchTerms(newSearchTerms);
  };

  return (
    <div>
      <h1>Custom Form</h1>

      <form onSubmit={(e) => submitSearchTerms(e)}>
          <label>
            Name:
            <input 
              type="text" 
              placeholder="Type Search Term"
              value={currentTerm}
              onChange={(e) => setCurrentTerm(e.target.value)} 
            />
            <button onClick={(e) => addTerm(e)}>Add term</button>
          </label>

          {
            searchTerms.map((el, index) => {
              return <p key={index} onClick={() => removeTerm(index)}>{el}</p>
            })
          }

          <label>
            Sort by:
            <select value={sortBy} onChange={(e) => setSortBy(e.target.value)} >
              <option value="best_match">Best Match</option>
              <option value="rating">Rating</option>
              <option value="review_count">Review Count</option>
              <option value="distance">Distance</option>
              <option value="random">Random</option>
            </select>
          </label>

          <input type="submit" value="Submit" />
        </form>
        <button onClick={() => toggleUi('landing')}>MAIN PAGE</button>
    </div>
  );
};

export default customForm;
