import React, { useContext, useState } from 'react';
import { Heading, Box } from 'grommet';

import CustomView from './CustomView';
import RandomView from './RandomView';
import { Store } from '../Store';
import Landing from './Landing';



// if location input is true, when clicking find me places or custom search
// automatically change location state



const mainView = ({ location, fetchSuccess }) => {
  const [locationInput, setLocationInput] = useState(false);
  const [newLocation, setNewLocation] = useState('');

  const { state, dispatch } = useContext(Store);
  const toggleUi = (ui) => dispatch ({ type: 'TOGGLE_UI', payload: ui });
  const toggleCustomForm = () => dispatch({ type: 'TOGGLE_CUSTOM_FORM' });

  return (
    <React.Fragment>
      {state.ui === 'landing' && (
        <React.Fragment>
          <Box height="60vh">
            <Landing 
              locationInput={locationInput}
              newLocation={newLocation}
              setNewLocation={setNewLocation}
              fetchSuccess={fetchSuccess} 
              location={location} 
            />
            {locationInput && 
            <Box>
              <button onClick={() => setLocationInput(false)}>Back</button>
            </Box>
            }
          </Box>
          <Box height="40vh" animation="slideUp">
            <Box
              onClick={fetchSuccess ? () => toggleUi('random') : null}
              background="status-warning"
              justify="center"
              align="center"
              height={locationInput ? "65%" : "50%"}
            >
              <Heading textAlign="center" size="small" level="3">
                FIND ME PLACES
              </Heading>
            </Box>
            <Box
              onClick={fetchSuccess ? () => toggleUi('custom') : null}
              background="accent-4"
              justify="center"
              align="center"
              height="35%"
            >
              <Heading textAlign="center" size="small" level="3">
                CUSTOM SEARCH
              </Heading>
            </Box>

            {!locationInput  &&
              <Box 
                onClick={() => setLocationInput(true)}
                background="dark-1" 
                justify="center" 
                height="15%" 
              >
                <Heading 
                  alignSelf="center"
                  textAlign="center"
                  size="xsmall" 
                  margin="none" 
                  level="6" 
                >
                  WRONG LOCATION?
                </Heading>
              </Box>
            }

          </Box>
        </React.Fragment>
      )}
      {state.ui === 'custom' && 
        <CustomView 
          toggleCustomForm={toggleCustomForm}
          customView={state.customView}
          location={location} 
          toggleUi={toggleUi}
          data={state.data}
        />
      } 
      {state.ui === 'random' && 
        <RandomView 
          location={location} 
          toggleUi={toggleUi} 
          data={state.data}
        />
      }
    </React.Fragment>
  );
};

export default mainView;
