import React, { Component } from 'react';
import Main from './components/main';
import ListItem from './components/listItem';

class App extends Component {
  state = {
    data: [],
    term: '',
    responseToPost: ''
  };

  // handleSubmit = async e => {
  //   e.preventDefault();
  //   const response = await fetch('/api/world', {
  //     method: 'POST',
  //     headers: {
  //       'Content-Type': 'application/json',
  //     },
  //     body: JSON.stringify({ post: this.state.post }),
  //   });
  //   const body = await response.text();
  //   this.setState({ responseToPost: body });
  // };

  // API GET REQUEST FETCHES LOCAL BUSINESSES FROM COORDINATES
  getJson = async e => {
    e.preventDefault();
    const response = await fetch('/getJson', {
      method: 'GET'
    });
    const body = await response.text();
    const businesses = JSON.parse(body);
    this.setState({ data: businesses.businesses }); 
  };

  logState = (e) => {
    e.preventDefault();
    console.log('LOGGING COMPONENT STATE');
    console.log(this.state.data);
  }

  // INPUT CONTENT SENT TO BACKEND SERVER.JS
  searchTerm = async e => {
    e.preventDefault();
    const response = await fetch('/search', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ term: this.state.term })
    });

    //const body = await response.text();
    //this.setState({ responseToPost: body });
  } 

  // HANDLE INPUT CHANGE UPDATING STATE
  handleChange = e => {
    this.setState({ term: e.target.value });
  };

  render() {
    return (
      <div>
        <Main />
        <form>
          <p>
            <strong>Test backend fetching</strong>
          </p>
          <button onClick={this.getJson}>Test</button>
          <button onClick={this.logState}>Console log component state</button>
        </form>

        <form onSubmit={this.searchTerm}>
          <label>
            Name:
            <input type="text" value={this.state.term} onChange={this.handleChange} />
          </label>
          <input type="submit" value="Submit" />
        </form>

        <ul>
          {this.state.data.length > 0 
            ? 
              this.state.data.map((item, index) => {
                return (
                  <li key={index}><ListItem item={item} /></li>
                )
              })
            :
              null
          }
        </ul>
      </div>
    );
  }
}
export default App;
