import React, { useContext } from 'react';
import { Heading, Box } from 'grommet';

import CustomView from './CustomView';
import RandomView from './RandomView';
import { Store } from '../Store';
import Landing from './Landing';

const mainView = ({ location }) => {
  const { state, dispatch } = useContext(Store);
  const toggleUi = (ui) => dispatch ({ type: 'TOGGLE_UI', payload: ui });
  const toggleCustomForm = () => dispatch({ type: 'TOGGLE_CUSTOM_FORM' });

  return (
    <React.Fragment>
      {state.ui === 'landing' && (
        <React.Fragment>
          <Box height="60vh">
            <Landing location={location} />
          </Box>
          <Box height="40vh" animation="slideUp">
            <Box
              onClick={() => toggleUi('random')}
              background="status-warning"
              justify="center"
              align="center"
              height="60%"
            >
              <Heading textAlign="center" size="small">
                FIND ME PLACES
              </Heading>
            </Box>
            <Box
              onClick={() => toggleUi('custom')}
              background="accent-4"
              justify="center"
              align="center"
              height="40%"
            >
              <Heading textAlign="center" level="2">
                CUSTOM SEARCH
              </Heading>
            </Box>
          </Box>
        </React.Fragment>
      )}
      {state.ui === 'custom' && 
        <CustomView 
          toggleCustomForm={toggleCustomForm}
          customView={state.customView}
          location={location} 
          toggleUi={toggleUi}
          data={state.data}
        />
      } 
      {state.ui === 'random' && 
        <RandomView 
          location={location} 
          toggleUi={toggleUi} 
          data={state.data}
        />
      }
    </React.Fragment>
  );
};

export default mainView;
