import React, { Component } from 'react';
import Main from './components/main';
import ListItem from './components/listItem';

class App extends Component {
  state = {
    data: [],
    term: '',
    sortBy:'best_match',
    distance: ''
  };

  // API GET REQUEST FETCHES LOCAL BUSINESSES FROM COORDINATES
  getJson = async e => {
    e.preventDefault();
    const response = await fetch('/getJson');
    const businesses = await response.json();
    this.setState({ data: businesses.businesses });
  };

  logState = (e) => {
    e.preventDefault();
    console.log('[ LOGGING STATE ]');
    console.log(this.state);
  };

  // GET REQUEST WITH TERM
  searchTerm = async e => {
    e.preventDefault();
    const response = await fetch('/search', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ 
        term: this.state.term,
        sort_by: this.state.sortBy,
        radius: this.state.distance * 1000
      })
    });
    const businesses = await response.json();
    this.setState({ data: businesses.businesses });
  };

  // HANDLE INPUT CHANGE UPDATING STATE
  handleChange = e => {
    this.setState({ term: e.target.value });
  };

  // HANDLE TYPE OF SORTING
  sorting = e => {
    this.setState({ sortBy: e.target.value });
  };

  // HANDLE DISTANCE SETTING STATE
  setDistance = e => {
    this.setState({ distance: e.target.value });
  }

  render() {
    return (
      <div>
        <Main />
        <form>
          <p>
            <strong>Test backend fetching</strong>
          </p>
          <button onClick={this.getJson}>Test</button>
          <button onClick={this.logState}>Console log component state</button>
        </form>

        {/* FUNCION DE ON SUBMIT QUE AGARRE LOS VALORES - NO VARIAS FUNCIONES */}
        <form onSubmit={this.searchTerm}>
          <label>
            Name:
            <input type="text" value={this.state.term} onChange={this.handleChange} />
          </label>

          <label>
            Sort by:
            <select value={this.state.sortBy} onChange={this.sorting} >
              <option value="best_match">Best Match</option>
              <option value="rating">Rating</option>
              <option value="review_count">Review Count</option>
              <option value="distance">Distance</option>
            </select>
          </label>

          <label>
            Distance en KMs:
            <input type="number" value={this.state.distance} onChange={this.setDistance} />
          </label>

          <input type="submit" value="Submit" />
        </form>

        <ul>
          {this.state.data.length > 0 || this.state.data === undefined
            ? 
              this.state.data.map((item, index) => {
                return (
                  <li key={index}><ListItem item={item} /></li>
                )
              })
            :
              null
          }
        </ul>
      </div>
    );
  }
}
export default App;
