import React from 'react';
import { Box } from 'grommet';

// import ResultList from './ResultList';
// import ResultHeader from './ResultHeader';

import Loadable from 'react-loadable';

const ResultList = Loadable({
  loader: () => import('./ResultList'),
  loading() {
    return <div>Loading...</div>
  }
});

const ResultHeader = Loadable({
  loader: () => import('./ResultHeader'),
  loading() {
    return <div>Loading...</div>
  }
});

const resultView = ({ 
  data, 
  toggleUi, 
  isCustom, 
  location, 
  randomSorting, 
  customSearchData, 
  toggleCustomForm,
}) => {

  return (
    <Box height="100vh">
      <ResultHeader 
        toggleUi={toggleUi} 
        isCustom={isCustom} 
        location={location} 
        customSearchData={customSearchData}
        toggleCustomForm={toggleCustomForm}
      />
      <ResultList 
        data={data} 
        toggleUi={toggleUi} 
        isCustom={isCustom} 
        randomSorting={randomSorting}
        customSearchTerms={customSearchData}
      />
      <Box height="8%" justify="center">
          <div style={{
              opacity: 0.2,
              fontSize: 15,
              fontWeight: 'bold',
              textAlign: 'center',
              alignItems: 'center',
            }}
          >
            <a 
              style={{ 'textDecoration': 'none' }} 
              href="http://www.github.com/pgast" 
            >
              &lt;/&gt; pgast
            </a>
          </div>
      </Box>
    </Box>
  );
};

export default resultView;
