import React, { useState, useEffect } from 'react';
import { Box, Heading, TextInput, ThemeContext } from 'grommet';

const landing = ({ location, fetchSuccess, locationInput }) => {
  const [newLocation, setNewLocation] = useState('');

  useEffect(() => {
    setNewLocation('');
  }, [newLocation]);

  // useEffect(() => {
  //   if (newLocation === '' || newLocation === undefined) { 
  //     setNewLocation(location); 
  //   } else {
  //     let newerLocation = newLocation.charAt(0).toUpperCase() + newLocation.substring(1)
  //     setNewLocation(newerLocation);
  //   }
  // }, [newLocation]);

  // dispatch to state new location (if typed)


  return (
    <Box 
      animation="fadeIn"
      justify="center" 
      align="center" 
      height="60vh"
      width="100vw"
    >


    {/* IF LOCATION INPUT DISPLAY LOCATION INPUT VIEW */}
    {locationInput && (
      <Box 
        justify="center"
        height="100%"
        width="100%"
        pad="large"
      >       
        <ThemeContext.Extend value={{
          global: { focus: { border: { color: "status-warning" } } },
          textInput: { extend: { 
            textAlign: "center",
            truncate: false,
            color: "status-warning"
          } }
        }}>
          <TextInput
            onChange={(e) => setNewLocation(e.target.value)}
            placeholder="Type your new location" 
            border={{ color: 'dark-1' }}
            value={newLocation}
            focusIndicator
            size="large"
            plain
          />
        </ThemeContext.Extend>
        <Heading 
          textAlign="center"
          alignSelf="center"
          color="dark-1" 
          margin="none"
          size="small"
        >
          and choose your option
        </Heading>
      </Box>
    )}


    {/* IF FETCH SUCCESS && LOCATION INPUT IS FALSE DISPLAY WHERE TO EAT IN  */}
    {((fetchSuccess || fetchSuccess === '') && !locationInput) && (
      <Box 
        justify="center"
        height="100%"
        width="100%"
        pad="medium"
        >
          <Heading 
            alignSelf="center"
            color="dark-1" 
            margin="none"
            size="small"
          >
            Where to eat in
          </Heading>
          <Heading 
            color="status-warning" 
            textAlign="center"
            truncate={false} 
            margin="none"
            size="large"
          >
            {location}
          </Heading>
      </Box>
    )}


    {(!(fetchSuccess || fetchSuccess === '') && !locationInput) && (
      <Heading>unable to fetch</Heading>
    )}

    </Box>
  )
}

export default landing;
