import React, { useContext } from 'react';
import { Store } from '../Store';
import ResultView from './ResultView';
import CustomForm from './CustomForm';

const customView = ({ location, toggleUi, toggleCustomForm, data }) => {
  //const { state } = useContext(Store);

    return (
      <div>
        {state.customView.resultsMode 
          ? <ResultView 
            randomSorting={state.customView.sortBy === 'random' ? true : false}
            toggleCustomForm={toggleCustomForm}
            customSearchData={state.customView}
            toggleUi={toggleUi}
            location={location}
            data={data}
            isCustom={true}
            /> 
          : <CustomForm toggleUi={toggleUi}/>}

      </div>
    );
};

export default customView;
