import React from 'react';
import { Box, Anchor, Text, Heading } from 'grommet';
import { Previous } from 'grommet-icons';

const resultHeader = ({ 
  customSearchData, 
  toggleCustomForm, 
  location, 
  isCustom, 
  toggleUi 
}) => {
  return (
    <React.Fragment>
      {!isCustom && 
        <Box height="10%" background="status-warning" direction="row" justify="evenly">
          <Box width="20%">
            <Box 
              background={{ "color": "dark-1", "opacity": "weak"}}
              round="large" 
              width="44px" 
              pad="small" 
            >
              <Anchor 
                onClick={() => toggleUi('landing')}
                icon={<Previous />}
                alignSelf="center" 
                color="dark-1" 
                size="small" 
              />
            </Box>
          </Box>
          <Box width="80%">
            <Heading size="small">Eating in {location}</Heading>
          </Box>
        </Box>
      }

      {isCustom && 
        <Box height="60%" background="status-warning">
          <ul>
            {customSearchData.searchTerms.map((el, index) => {
              return <li key={index}>{el}</li>
            })} 
          </ul>         
          <h6>Sorting by: {customSearchData.sortBy}</h6>
          <button onClick={() => toggleCustomForm()}>EDIT SEARCH CRITERIA</button>
        </Box>
      }
    </React.Fragment>
  );
};

export default resultHeader;
