import React from 'react';

const styles = {
  border: '1px solid black',
  display: 'flex',
  flexDirection: 'row'
}

const resultHeader = ({ location, isCustom, toggleCustomForm, toggleUi, customSearchData }) => {
  return (
    <div style={styles}>
      <h3>Searching for eating options in {location}</h3>
      <h2>{isCustom ? 'Custom ' : 'Random '} Results Page</h2>
      <button onClick={() => toggleUi('landing')}>MAIN PAGE</button>

      {
        isCustom && 
        <div>
          <ul>
            {customSearchData.searchTerms.map((el, index) => {
              return <li key={index}>{el}</li>
            })} 
          </ul>         
          <h6>Sorting by: {customSearchData.sortBy}</h6>
          <button onClick={() => toggleCustomForm()}>EDIT SEARCH CRITERIA</button>
        </div>
      }
    </div>
  );
};

export default resultHeader;
