import React, { useReducer, createContext } from "react";

const initialState = {
  customView: {
    resultsMode: false,
    searchTerms: [],
    sortBy: 'Best Match'
  },
  data: [],
  ui: 'landing',
  inputLocation: ''
}

const randomSorting = () => {
  const criteria = ['best_match', 'rating', 'review_count', 'distance'];
  return criteria[Math.floor(Math.random() * 4)];
};


// changing post request to get request
const searchTerm = async (term, sort_by, newLocation, inputLocationMode) => {
    const response = await fetch(`/search/${term}/${sort_by}/${newLocation}/${inputLocationMode}`);
    const businesses = await response.json();
    return businesses.businesses;
  };


// WORKING SEARCH TERM FUNCTION WITH URL PARAMETERS
  // const searchTerm = async (term, sort_by, newLocation, inputLocationMode) => {
  //   const response = await fetch(`/search/${inputLocationMode}/${newLocation}`, {
  //     method: 'POST',
  //     headers: { 'Content-Type': 'application/json' },
  //     body: JSON.stringify({ 
  //       term,
  //       sort_by
  //     })
  //   });
  //   const businesses = await response.json();
  //   return businesses.businesses;
  // };


const fetchData = async (newLocation, searchInputs) => {
  const { sortBy, searchTerms, locationInput } = searchInputs;
  const sorting = sortBy === 'random' ? randomSorting() : sortBy



  if (searchTerms.length === 0) {
    const data = await searchTerm(null, sorting, newLocation, locationInput);

    return data;
  } else { 
    const termOne = searchTerms[0] !== undefined ? searchTerms[0] : null; 
    const termTwo = searchTerms[1] !== undefined ? searchTerms[1] : null; 
    const termThree = searchTerms[2] !== undefined ? searchTerms[2] : null; 

    let result = [];
    let dataOne = [];
    let dataTwo = [];
    let dataThree = [];

    if (termOne !== null) { dataOne = await searchTerm(termOne, sorting, newLocation, locationInput) };
    if (termTwo !== null) { dataTwo = await searchTerm(termTwo, sorting, newLocation, locationInput) };
    if (termThree !== null) { dataThree = await searchTerm(termThree, sorting, newLocation, locationInput) };

    if (dataOne.length !== 0) { result.push(dataOne) };
    if (dataTwo.length !== 0) { result.push(dataTwo) };
    if (dataThree.length !== 0) { result.push(dataThree) };

    return result;
  }
};

const fetchRandomData = async (newLocation, inputLocationMode) => {
  const response = await fetch(`/getRandom/${inputLocationMode}/${newLocation}`);
  const businesses = await response.json();
  return businesses.businesses;
};

function reducer(state, action) {
  let customViewCopy = { ...state.customView };

  switch(action.type) {
    case 'TOGGLE_CUSTOM_FORM':
      customViewCopy.resultsMode = !customViewCopy.resultsMode;
      return { ...state, customView: customViewCopy };

    case 'SUBMIT_SEARCH':
      customViewCopy = {
        searchTerms: action.payload.searchTerms,
        sortBy: action.payload.sortBy,
        resultsMode: true
      };
      return { ...state, customView: customViewCopy, data: fetchData(state.inputLocation, action.payload) };

    case 'FETCH_RANDOM_DATA':
      return { ...state, data: fetchRandomData(state.inputLocation, action.payload) }

    case 'TOGGLE_UI':
      customViewCopy = {
        resultsMode: false,
        searchTerms: [],
        sortBy: 'Best Match'
      };
      return { ...state, customView: customViewCopy, data: [], ui: action.payload };

    case 'UPDATE_LOCATION':
      return { ...state, inputLocation: action.payload };

    case 'CLEAR_LOCATION':
      return { ...state, inputLocation: '' };

    default:
      return state;
  }
};

export function StoreProvider(props) {
  const [state, dispatch] = useReducer(reducer, initialState)
  const value = {state, dispatch}
  return <Store.Provider value={value}>{props.children}</Store.Provider>
}

export const Store = createContext();
