import React, { useState } from 'react';
import Landing from './Landing';
import RandomView from './RandomView';
import CustomView from './CustomView';

const mainView = props => {
  const [display, setDisplay] = useState('landing');

  const switchView = view => {
    setDisplay(view);
  };

  return (
    <div>
      {
        display === 'landing' 
        ? (
        <div>
          <Landing location={props.location} />
          <h3 onClick={switchView.bind(this, 'random')}>RANDOM PICK</h3>
          <h3 onClick={switchView.bind(this, 'custom')}>CUSTOM SEARCH</h3>
        </div>
      ) : 
      null
      }
      {display === 'custom' ? <CustomView location={props.location}/> : null}

      {display === 'random' ? <RandomView location={props.location} /> : null}
    </div>
  );
};

export default mainView;
