import React, { useContext } from 'react';
import { Store } from '../Store';
import ResultView from './ResultView';
import CustomForm from './CustomForm';

const customView = ({ location }) => {
  const { state, dispatch } = useContext(Store);

  // TEST FUNCTION NOT IN PRODUCTION
  const changeMode = (e) => {
    e.preventDefault()
    return dispatch({
      type: 'TOGGLE',
      payload: 'toggle'
    })
  }

    return (
      <div>
        <h1>Custom View</h1>

        {state.customView.resultsMode 
          ? <ResultView 
            location={location}
            data={state.data}
            isCustom={true}
            randomSorting={state.customView.sortBy === 'random' ? true : false}/> 
          : <CustomForm />}

        <button onClick={(e) => changeMode(e)}>Toggle Results Mode</button>
      </div>
    );
};

export default customView;
