import React, { Component } from 'react';
import { Grommet, Box } from 'grommet';
import Request from 'request';

import MainView from './components/MainView';

class App extends Component {
  state = {
    coordinates: { lat: '', lon: '' },
    fetchSuccess: '',
    location: ''
  };

  geoFindMe = () => {
    console.log('geofindme')
    function success(position) {
      return {
        lat: position.coords.latitude,
        lon: position.coords.longitude
      };
    }

    function error() {
      return "fetchError";
    }

    if (!navigator.geolocation) {
      return "fetchError";
    } else {
      navigator.geolocation.getCurrentPosition(success, error);
    }
  }


  componentWillMount() {
    Request.get({ "url": "http://ip-api.com/json/" }, (error, response, body) => {
      if (error || JSON.parse(body).status === "fail") {

        this.setState({ fetchSuccess: false }); 
        return console.dir(error);    

      } else {

        const location = JSON.parse(body).city;
        this.setState({ 
          coordinates: this.geoFindMe(),
          fetchSuccess: true,
          location
        });
        console.log(this.state);
      }
    });
  };




