  //       } else {
  //         const location = JSON.parse(body).city;
  //         const lat = JSON.parse(body).lat;
  //         const lon = JSON.parse(body).lon;
  //         this.setState({ 
  //           location, 
  //           fetchSuccess: true,
  //           coordinates: { lat, lon } 
  //         });
  //       }
  //   });

  // };

  render() {
    return (
      <Grommet>
        <Box width="100vw" height="100vh">
          <MainView 
            fetchSuccess={this.state.fetchSuccess}
            coordinates={this.state.coordinates}
            location={this.state.location} 
          />
        </Box>
      </Grommet>
    );
  }
}
export default App;
