import React from 'react';

import { Box, Text } from 'grommet';

const ListItem = ({ item, removeItem }) => {
  return (
    <Box 
      background="accent-4" 
      align="center"
      round="small" 
      height="20%" 
      pad="medium"
      width="85%" 
    >
      <Text weight="bold" truncate>{item.name}</Text>

      <Box direction="row" justify="around" width="60%" pad="medium">
        <Text size="small">{item.categories[0].title}</Text>
        <Text size="small">{(item.distance / 1000).toFixed(1)} kms</Text>
      </Box>

      <Box direction="row" justify="around" width="100%">
        <Text size="small"><a href={`tel:${item.phone}`}>Hablar a negocio</a></Text>
        <Text size="small">More Info</Text>
        <button onClick={() => removeItem()}>Delete Item</button>
      </Box>
    </Box>
  );
};

export default ListItem;
