import React from 'react';

import { Box, Text, Heading, Button } from 'grommet';

const choiceView = ({ item, toggleUi }) => {
  return (
    <Box background="status-warning" width="100%" height="100%" align="center">
      <Box 
        height="85%"
        width="100%"
        pad={{ top: "xlarge" }}
        align="center"
        justify="around"
      >
        <Text textAlign="center" weight="bold" size="large">You should try</Text>
        <Heading color="light-1">{item.name}</Heading>
        <Text textAlign="center" weight="bold" size="small" margin="large">
          You ran out of options, try again or tweak your custom search parameters
        </Text>
      </Box>
      <Box height="15%">
        <Button 
          onClick={() => toggleUi('landing')} 
          label="Try again!" 
          color="dark-1" 
          primary
        />
      </Box>
    </Box>
  )
}

export default choiceView;
