import React, { Component } from 'react';
import { Grommet, Box } from 'grommet';
import Request from 'request';

import MainView from './components/MainView';

class App extends Component {
  state = {
    coordinates: { lat: '', lon: '' },
    fetchSuccess: '',
    location: ''
  }; 

  geoFindMe() {
    function success(position) {
      const lat = position.coords.latitude;
      const lon = position.coords.longitude;
      return { lat, lon };
    }

    function error() {
      return "error";
    }

    if (!navigator.geolocation) {
      error();
    } else {
      navigator.geolocation.getCurrentPosition(success, error);
    }
  }

  componentWillMount() {
    // fetch city
    Request.get({ "url": "http://ip-api.com/json/" }, (error, response, body) => {
        if (error || JSON.parse(body).status === "fail") {
          this.setState({ fetchSuccess: false }); 
        } else {
          const location = JSON.parse(body).city;
          this.setState({ 
            location, 
            fetchSuccess: true,
          });
        }
    });

    // fetch coordinates
    this.setState({ coordinates: this.geoFindMe() });
    console.log(this.state.coordinates);
  };

  // componentWillMount() {
  //   Request.get({ "url": "http://ip-api.com/json/" }, (error, response, body) => {
  //       if (error || JSON.parse(body).status === "fail") {
  //         this.setState({ fetchSuccess: false }); 
  //         return console.dir(error);    
  //       } else {
  //         const location = JSON.parse(body).city;
  //         const lat = JSON.parse(body).lat;
  //         const lon = JSON.parse(body).lon;
  //         this.setState({ 
  //           location, 
  //           fetchSuccess: true,
  //           coordinates: { lat, lon } 
  //         });
  //       }
  //   });
  // };

  render() {
    return (
      <Grommet>
        <Box width="100vw" height="100vh">
          <MainView 
            fetchSuccess={this.state.fetchSuccess}
            coordinates={this.state.coordinates}
            location={this.state.location} 
          />
        </Box>
      </Grommet>
    );
  }
}
export default App;
