import React, { useState, useEffect } from 'react';
import { Box, Heading, TextInput, Button, ThemeContext } from 'grommet';
import { FormSearch } from 'grommet-icons';

const landing = ({ location, fetchSuccess }) => {
  const [newLocation, setNewLocation] = useState();
  useEffect(() => { setNewLocation(location) }, [location]);

  useEffect(() => {
    if (newLocation === '' || newLocation === undefined) { 
      setNewLocation(location); 
    } else {
      let newerLocation = newLocation.charAt(0).toUpperCase() + newLocation.substring(1)
      setNewLocation(newerLocation);
    }
  }, [newLocation]);

  // dispatch to state new location (if typed)


  return (
    <Box 
      animation="fadeIn"
      justify="center" 
      align="center" 
      height="60vh"
      width="100vw"
    >
      {fetchSuccess || fetchSuccess === '' ? 
        (
          <React.Fragment>
            <Box 
              width="100%"
              height="80%"
              justify="center"
              pad="medium"
            >
              <Heading 
                color="dark-1" 
                size="small"
                margin="none"
                alignSelf="center"
              >
                Where to eat in
              </Heading>
              <Heading 
                color="status-warning" 
                textAlign="center"
                size="large"
                truncate={false} 
                margin="none"
              >
                {newLocation}
              </Heading>
            </Box>

            <Box 
              height="20%"
              width="100%"
              direction="row"
              align="center"
              justify="center"
            >
              <Box width="210px">
                <ThemeContext.Extend value={{}}>
                  <TextInput
                    onChange={(e) => setNewLocation(e.target.value)}
                    border={{ color: 'dark-1' }}
                    placeholder="Wrong location? Fix it here" 
                    size="small"
                    plain
                  />
                </ThemeContext.Extend>
              </Box>
              <Box>
                <ThemeContext.Extend value={{}}>
                  <Button 
                    color="status-warning"
                    icon={<FormSearch />}
                    alignSelf="center"
                    margin="none"
                    border={{ color: 'dark-1' }}
                  />
                </ThemeContext.Extend>
              </Box>
            </Box> 
          </React.Fragment>
        )
        : (
          <Heading>unable to fetch</Heading>
        )
      }

    </Box>
  )
}

export default landing;
