import React, { useContext } from 'react';
import CustomView from './CustomView';
import RandomView from './RandomView';
import { Store } from '../Store';
import Landing from './Landing';

import { Heading, Box } from 'grommet';

const mainView = ({ location }) => {
  const { state, dispatch } = useContext(Store);

  const toggleUi = (ui) => {
    return dispatch ({
      type: 'TOGGLE_UI',
      payload: ui
    });
  };

  const toggleCustomForm = () => {
    return dispatch({
      type: 'TOGGLE_CUSTOM_FORM',
    });
  };

  return (
    <React.Fragment>
      {state.ui === 'landing' ? (
        <React.Fragment>
          <Box height="60vh">
            <Landing location={location} />
          </Box>

          <Box
            height="40vh"
            border={{ color: 'neutral-1', size: 'large' }}
          >
            <Box
              background="light-3"
            >
              <Heading onClick={() => toggleUi('random')}>RANDOM PICK</Heading>
            </Box>

            <Box
              background="dark-3"
            >
              <Heading onClick={() => toggleUi('custom')}>CUSTOM SEARCH</Heading>
            </Box>
          </Box>
        </React.Fragment>
      ) : 
        null
      }


      {state.ui === 'custom' && 
        <CustomView 
          toggleCustomForm={toggleCustomForm}
          customView={state.customView}
          location={location} 
          toggleUi={toggleUi}
          data={state.data} 
        />
      } 


      {state.ui === 'random' && 
        <RandomView 
          location={location} 
          toggleUi={toggleUi} 
          data={state.data}
        />
      }
    </React.Fragment>
  );
};

export default mainView;
