import React, { useEffect } from 'react';
import ResultView from './ResultView';
import CustomForm from './CustomForm';

const customView = ({ location, toggleUi, toggleCustomForm, data, customView }) => {
  useEffect(() => {
    console.log('custom view component reloaded, because data changed')
  }, [data]);  

  return (
      <div>
        {customView.resultsMode 
          ? <ResultView 
            randomSorting={customView.sortBy === 'random' ? true : false}
            toggleCustomForm={toggleCustomForm}
            customSearchData={customView}
            toggleUi={toggleUi}
            location={location}
            isCustom={true}
            data={data}
            /> 
          : <CustomForm toggleUi={toggleUi} customView={customView}/>}
      </div>
    );
};

export default customView;
