import React from 'react';

import { Box, Text, Anchor } from 'grommet';

const ListItem = ({ item, removeItem }) => {
  return (
    <Box 
      background="accent-4" 
      elevation="small"
      align="center"
      round="small" 
      height="25%" 
      width="85%" 
    >
      <Text weight="bold" margin={{top: "small"}} truncate>{item.name}</Text>

      <Box direction="row" justify="center" gap="medium" width="100%" margin="small">
        <Text size="small">{item.categories[0].title}</Text>
        <Text size="small">{(item.distance / 1000).toFixed(1)} kms</Text>
      </Box>

      <Box direction="row" justify="around" width="100%">
        <Anchor size="small" href={item.url} primary label="More Info" />
        <button onClick={() => removeItem()}>Delete Item</button>
      </Box>
    </Box>
  );
};

export default ListItem;
