import React, { useState, useContext } from 'react';
import { Store } from '../Store';

import { FormField, TextInput, Button, Select, Box, Heading } from 'grommet';
import FormPrevious from 'grommet-icons';

const customForm = ({ toggleUi, customView }) => {
  const { dispatch } = useContext(Store);
  const [searchTerms, setSearchTerms] = useState(customView.searchTerms);
  const [sortBy, setSortBy] = useState(customView.sortBy);
  const [currentTerm, setCurrentTerm] = useState("");

  const submitSearchTerms = (e) => {
    e.preventDefault();
    let payload = { sortBy, searchTerms };
    return dispatch ({ type: 'SUBMIT_SEARCH', payload });
  };

  const addTerm = (e) => {
    e.preventDefault();
    if (currentTerm !== '') {
      if (searchTerms.length < 3) {
        let newTerms = [...searchTerms];
        newTerms.push(currentTerm);
        setSearchTerms(newTerms);
        setCurrentTerm('');
      }
    }
  };

  const removeTerm = (inputIndex) => {
    const newSearchTerms = [...searchTerms].filter((el, inx) => inx !== inputIndex);
    setSearchTerms(newSearchTerms);
  };

  return (
    <Box background="accent-4" height="100vh" width="100vw" animation="fadeIn">
      <Box height="84vh">
        {/* header area back button and custom search title */}
        <Box>
          <Button onClick={() => toggleUi('landing')} icon={<FormPrevious />}/>
          <h1>Custom Form</h1>
        </Box>

        {/* form area with inputs */}
        <Box>
          <form>
          <FormField label="Search for:">
            <TextInput 
              onChange={(e) => setCurrentTerm(e.target.value)}
              placeholder="e.g. pizza, tacos, bar" 
              value={currentTerm}
            />
          </FormField>

          <Button onClick={(e) => addTerm(e)} label="Add term" />


          {searchTerms.map((el, index) => {
            return <p key={index} onClick={() => removeTerm(index)}>{el}</p>
          })}


          <Select
            options={['Best Match', 'Rating', 'Distance', 'Random']}
            onChange={({ option }) => setSortBy(option)}
            value={sortBy}
          />

          </form>
        </Box>
      </Box>

      <Box
        onClick={(e) => submitSearchTerms(e)}
        background="status-warning" 
        justify="center"
        height="16vh" 
      >
        <Heading textAlign="center" level="2">
          FIND ME PLACES
        </Heading>
      </Box>
    </Box>
  );
};

export default customForm;
