import React from 'react';

import { Box, Text, ThemeContext, Button } from 'grommet';

const ListItem = ({ item, removeItem }) => {
  return (
    <Box 
      background="accent-4" 
      align="center"
      round="small" 
      height="27%" 
      width="85%" 
      justify="around"
    >
      <Text textAlign="center" weight="bold" margin={{top: "small"}}>{item.name}</Text>

      <Box direction="row" justify="center" gap="medium" width="100%">
        <Text size="small">{item.categories[0].title}</Text>
        <Text size="small">{(item.distance / 1000).toFixed(1)} kms</Text>
      </Box>

      <Box direction="row" justify="around" width="100%" >
        <ThemeContext.Extend value={{ global: { text: { medium: { height: "11px" } } } }}>
          <Button color="dark-1" size="height" href={item.url} primary label="More Info" />
          <Button color="dark-1" size="small" onClick={() => removeItem()} label="Delete Item"/>
        </ThemeContext.Extend>
      </Box>
    </Box>
  );
};

export default ListItem;
