import React from 'react';

import ResultView from './ResultView';
import CustomForm from './CustomForm';

const customView = ({ location, toggleUi, toggleCustomForm, data, customView, locationInput }) => {
  return (
    <div>
      {customView.resultsMode ? 
        <ResultView 
          randomSorting={customView.sortBy === 'random' ? true : false}
          toggleCustomForm={toggleCustomForm}
          customSearchData={customView}
          toggleUi={toggleUi}
          location={location}
          isCustom={true}
          data={data}
        /> 
        : 
        <CustomForm 
          locationInput={locationInput}
          customView={customView} 
          toggleUi={toggleUi} 
        />}
    </div>
  );
};

export default customView;
